import React, {Component} from "react";
import Cell from "./Cell";
import './Board.css';


/** Game board of Lights out.
 *
 * Properties:
 *
 * - nrows: number of rows of board
 * - ncols: number of cols of board
 * - chanceLightStartsOn: float, chance any cell is lit at start of game
 *
 * State:
 *
 * - hasWon: boolean, true when board is all off
 * - board: array-of-arrays of true/false
 *
 *    For this board:
 *       .  .  .
 *       O  O  .     (where . is off, and O is on)
 *       .  .  .
 *
 *    This would be: [[f, f, f], [t, t, f], [f, f, f]]
 *
 *  This should render an HTML table of individual <Cell /> components.
 *
 *  This doesn't handle any clicks --- clicks are on individual cells
 *
 **/

class Board extends Component {

  constructor(props) {
    super(props);
    this.state={
      hasWon: false,
      board: this.createBoard()
    }
    // TODO: set initial state
    this.flipCellsAround=this.flipCellsAround.bind(this);
  }

  /** create a board nrows high/ncols wide, each cell randomly lit or unlit */

  createBoard() {
    let board = [];
    for(let i=0;i<this.props.nrows;i++){
      let row=[]
      for(let i=0;i<this.props.ncols;i++){
        let light=Math.random()*this.props.chanceLightStartsOn>1?true:false;
        row.push(light)
      }
      board.push(row)
    }

    return board
  }

  /** handle changing a cell: update board & determine if winner */

  flipCellsAround(coord) {
    let {ncols, nrows} = this.props;
    let newBoard = [...this.state.board];
    let [y, x] = coord.split("-").map(Number);

    function flipCell(y, x) {
      // if this coord is actually on board, flip it

      if (x >= 0 && x < ncols && y >= 0 && y < nrows) {
        newBoard[y][x] = !newBoard[y][x];
      }
    }

    // TODO: flip this cell and the cells around it
    flipCell(y, x);
    flipCell(y+1, x);
    flipCell(y-1, x);
    flipCell(y, x-1);
    flipCell(y, x+1);

    // win when every cell is turned off
    // TODO: determine is the game has been won
    let lightCells=0;
    newBoard.forEach(element => {
      element.forEach(element => {
        lightCells+=element?1:0;
      });
    });
    console.log(newBoard)
    this.setState({board: newBoard, hasWon: lightCells===0});
  }


  /** Render game board or winning message. */

  render() {

    // if the game is won, just show a winning msg & render nothing else

    // TODO

    // make table board

    return(
      <table className="Board">
        <tbody>
        {this.state.board.map((arr,y)=>
          <tr className="CellRow">
            {arr.map((cell,x)=><Cell key={y+'-'+x} id={y+'-'+x} isLit={cell} flipCellsAroundMe={this.flipCellsAround}/>)}
          </tr>
          )}
        </tbody>
      </table>
    )
  }
}


export default Board;
